import { TaskModuleResponseBase } from './task-module-response-base.js';
import { TaskModuleTaskInfo } from './task-module-task-info.js';
import '../attachment/attachment.js';

/**
 *
 * An interface representing TaskModuleContinueResponse.
 * Task Module Response with continue action.
 *
 * @extends TaskModuleResponseBase
 */
type TaskModuleContinueResponse = TaskModuleResponseBase & {
    type: 'continue';
    /**
     * @member {TaskModuleTaskInfo} [value] The JSON for the Adaptive card to
     * appear in the task module.
     */
    value?: TaskModuleTaskInfo;
};

export type { TaskModuleContinueResponse };
