import { IListStorage } from './storage.js';

/**
 * An in-memory list storage.
 */
declare class ListLocalStorage<T = any> implements IListStorage<T> {
    protected readonly _list: T[];
    constructor(data?: T[]);
    get(key: number): T | undefined;
    set(key: number, value: T): void;
    delete(key: number): void;
    push(value: T): void;
    pop(): T | undefined;
    values(): T[];
    length(): number;
    where(predicate: (value: T, index: number) => boolean): T[];
}

export { ListLocalStorage };
